<?php

$input = "



";

//$input = "ULL
//RRDDD
//LURDL
//UUUUD";

$input = explode("\n", $input);

$current_button = [2, 2];
$keypad = [
    1 => [1 => 1, 2 => 2, 3 => 3],
    2 => [1 => 4, 2 => 5, 3 => 6],
    3 => [1 => 7, 2 => 8, 3 => 9],
];

$x = 0;
$y = 0;

$code = [];

foreach ($input as $line) {
    $line = str_split($line);

    foreach ($line as $letter) {
        $x = $current_button[0];
        $y = $current_button[1];
        switch ($letter) {
            case "U":
                $y = move($y, -1);
                break;
            case "R":
                $x = move($x, 1);
                break;
            case "D":
                $y = move($y, 1);
                break;
            case "L":
                $x = move($x, -1);
                break;
        }
        $current_button = [$x, $y];
    }

    $key_row = $current_button[1];
    $key_col = $current_button[0];
    $key = $keypad[$key_row][$key_col];
    $code[] = $key;
}

echo "Day 2 answer 1: " . (implode("", $code)).PHP_EOL;

function move($position, $direction)
{
    $position += $direction;
    if ($position < 1) {
        $position = 1;
    }
    if ($position > 3) {
        $position = 3;
    }

    return $position;
}